import React, { useState, useEffect } from "react";
import walmartData from "./../../walmartData.json" 


function SecondBlockComponent() {

    const [data,setData] = useState([]);
    const [newData, setNewData] = useState([]); 

    const [button, setButton] = useState('0');
    const [text, setText] = useState('0');

    const handleFirstTextChange = () => {
          setText('1'); 
        };

    const handleSecondTextChange = () => {
          setText('2');  
        };

    const handleRandomTextChange = () => {
          setText('3');  
        };

    const updateData = (par) => {
        switch(par) {
            case '1':
                return console.log("Jeden")
            case '2':
                return console.log("Dwa")
        }
    }

    useEffect(() => {
        setData(walmartData); 
        if(button > 0) {
           console.log("button", button)
           updateData(button);
        }
    }) 

    return (
        <>
        <div>
            <button name="zastap" value='1' onClick={(e) => setButton(e.target.value)}>ZASTĄP</button>
            <button name="doklej" value='2' onClick={(e) => setButton(e.target.value)}>DOKLEJ</button>
        </div>
        <div>
         <input type="radio" value={text === '1'} name='foo' onChange={handleFirstTextChange}/> Pierwsza treść
         <input type="radio" value={text === '2'} name='foo' onChange={handleSecondTextChange}/> Druga treść
         <input type="radio" value={text === '3'} name='foo' onChange={handleRandomTextChange}/> Losowa treść
        </div>
        <div>
            <h1>{button}</h1>
            <h2>{text}</h2>
        </div>
        </>
    )
}

export default SecondBlockComponent
