import React, { useState, useEffect } from "react";
import walmartData from "./../../walmartData.json" 

function SecondBlockComponent() {

    const arr = []

    const [data,setData] = useState([]);
    const [newData, setNewData] = useState([]); 

    const [button, setButton] = useState('0');
    const [text, setText] = useState('0');

    const handleFirstTextChange = () => { setText('1'); };
    const handleSecondTextChange = () => { setText('2'); };
    const handleRandomTextChange = () => { setText('3'); };

    const updateData = (parButton, parText) => {
        if (parButton == 1){
                console.log('AA1')
                switch(parText) {
                    case '1':
                        console.log('Txx')
                        if (newData.length == 0) {
                            setNewData(() => data[0])
                            data.slice(2);
                            console.log("data", data)
                        } else {
                            console.log("BBR", newData.length);
                        }
                    case '2':
                        return console.log('T2');
                    case '3':
                        return console.log('T3');
                }
            }
        if (parButton == 2) {
                console.log('AA2')
                switch(parText) {
                    case '1':

                        return console.log('T1X');
                    case '2':
                        return console.log('T2X');
                    case '3':
                        return console.log('T3X');
                }
            }
        }

    useEffect(() => {
        setData(walmartData.walmart); 
    }, [])    

    useEffect(() => {
        if(button > 0) {
           updateData(button, text);
           setButton('0');
           console.log("EFFECT NEW DAYA", newData);
           console.log(data);   
        }
    }) 

    return (
        <>
        <div>
            <button name="zastap" value='1' onClick={(e) => setButton(e.target.value)}>ZASTĄP</button>
            <button name="doklej" value='2' onClick={(e) => setButton(e.target.value)}>DOKLEJ</button>
        </div>
        <div>
         <input type="radio" value={text === '1'} name='foo' onChange={handleFirstTextChange}/> Pierwsza treść
         <input type="radio" value={text === '2'} name='foo' onChange={handleSecondTextChange}/> Druga treść
         <input type="radio" value={text === '3'} name='foo' onChange={handleRandomTextChange}/> Losowa treść
        </div>
        <div>
            {/* <h1>{button}</h1> */}
            {/* <h2>{text}</h2> */}
        </div>
        <div >
            {newData.id}
            {newData.tresc}
        </div>
        </>
    )
}

export default SecondBlockComponent
