function SecondBlockComponent() {

    const [button, setButton] = useState('0');
    const didMount = useRef(false);

    const handleButtons = (e) => {
        e.preventDefault();
        setButton(e.target.value);
        console.log("button", button);
        console.log(e.target.value)
    }

        useEffect(() => {
            if(!didMount.current)
